mapper.select:
		 select lp.* 
		 from
		 lte_plane lp 
		 where 1=1

            and lp.projId = #{map.projId}


            and lp.mENodeBID like '%${map.mENodeBID}%'


            and lp.testTime >=  #{map.beginTime}


            and lp.testTime <=  #{map.endTime}


            and lp.testTime 
            between #{map.beginTime}
            and     #{map.endTime}

		 order by lp.testTime desc

 select 
    id as  "planId",
    testTime as "testDate"
 from
 lte_plane lp 
 where 1=1

	    and lp.testTime =#{nowDate}

    and dealPersonId =#{userId}
    and projId =#{projId}

mapper.delete:
		 delete from lte_plane
		 where id in

            #{item}

mapper.update:
update lte_plane 
set mENodeBID=#{mENodeBID}
,mBaseStationName=#{mBaseStationName}
,mBaseStationType=#{mBaseStationType}
,mAltitude=#{mAltitude}
,mLongitude=#{mLongitude} 
,mLatitude=#{mLatitude} 

,testPerson=#{testPerson} 
,testPersonPhone=#{testPersonPhone} 
,backPerson=#{backPerson} 
,backPersonPhone=#{backPersonPhone} 
,testTime=#{testTime} 
,mTac=#{mTac} 
,updateTime=#{updateTime} 
,dealPersonId=#{dealPersonId} 

where id=#{id}

update 
lte_plane 
set status=#{statusM} 
where id=#{id}
