mapper.select:
select su.id,su.username,su.nickname,su.phone ,su.telephone,su.email,su.birthday ,su.sex,su.status,su.createTime
from sys_user su where 1=1
and su.username = #{map.username}
and su.nickname = #{map.nickname}
and su.createTime >= str_to_date(#{map.beginTime},'%Y-%m-%d %H:%i:%s')
order by su.createTime desc

mapper.delete:
		 delete from sys_user
		 where id in

            #{item}
